+import kotlinx.coroutines.flow.MutableStateFlow
+import kotlinx.coroutines.flow.StateFlow
+import kotlinx.coroutines.flow.asStateFlow
+
+/**
+ * Repository that provides access to test and question data
+ */
+class TestRepository {
+ 
+ // Mock data for testing
+ private val _mockTests = MutableStateFlow<List<MockTest>>(
+ listOf(
+ MockTest(
+ name = "Basic Android Concepts",
+ difficulty = TestDifficulty.EASY,
+ questions = generateSampleQuestions(10, "Android", "Basics"),
+ timeLimit = 30
+ ),
+ MockTest(
+ name = "Advanced Android Development",
+ difficulty = TestDifficulty.MEDIUM,
+ questions = generateSampleQuestions(20, "Android", "Advanced"),
+ timeLimit = 60
+ ),
+ MockTest(
+ name = "Expert Android Mastery",
+ difficulty = TestDifficulty.HARD,
+ questions = generateSampleQuestions(30, "Android", "Expert"),
+ timeLimit = 90
+ )
+ )
+ )
+ 
+ val mockTests: StateFlow<List<MockTest>> = _mockTests.asStateFlow()
+ 
+ // Sample user stats
+ private val _userStats = MutableStateFlow(
+ UserStats(
+ questionsAnswered = 120,
+ correctAnswers = 95,
+ streak = 7
+ )
+ )
+ 
+ val userStats: StateFlow<UserStats> = _userStats.asStateFlow()
+ 
+ // Get mock test by difficulty
+ fun getTestsByDifficulty(difficulty: TestDifficulty): List<MockTest> {
+ return _mockTests.value.filter { it.difficulty == difficulty }
+ }
+ 
+ // Get a test by ID
+ fun getTestById(id: String): MockTest? {
+ return _mockTests.value.find { it.id == id }
+ }
+ 
+ // Helper to generate sample questions
+ private fun generateSampleQuestions(count: Int, subject: String, topic: String): List<Question> {
+ val questions = mutableListOf<Question>()
+ 
+ repeat(count) { index ->
+ questions.add(
+ Question(
+ text = "Sample question #${index + 1} about $subject $topic?",
+ options = listOf(
+ "Option A",
+ "Option B",
+ "Option C",
+ "Option D"
+ ),
+ correctOptionIndex = (0..3).random(),
+ explanation = "This is the explanation for question #${index + 1}",
+ difficulty = when {
+ index % 3 == 0 -> QuestionDifficulty.EASY
+ index % 3 == 1 -> QuestionDifficulty.MEDIUM
+ else -> QuestionDifficulty.HARD
+ },
+ type = QuestionType.MULTIPLE_CHOICE,
+ subject = subject,
+ topic = topic
+ )
+ )
+ }
+ 
+ return questions
+ }
+ 
+ // Singleton pattern
+ companion object {
+ @Volatile
+ private var INSTANCE: TestRepository? = null
+ 
+ fun getInstance(): TestRepository {
+ return INSTANCE ?: synchronized(this) {
+ val instance = TestRepository()
+ INSTANCE = instance
+ instance
+ }
+ }
+ }
