+@Composable
+fun MockTestSelectionScreen(
+ onNavigateBack: () -> Unit,
+ onTestSelected: (String) -> Unit,
+ onSettingsClick: () -> Unit,
+ repository: TestRepository = TestRepository.getInstance()
+) {
+ val allTests by repository.mockTests.collectAsState()
+ var selectedTabIndex by remember { mutableIntStateOf(0) }
+ 
+ // Filter tests by difficulty based on selected tab
+ val filteredTests = when (selectedTabIndex) {
+ 0 -> allTests.filter { it.difficulty == TestDifficulty.EASY }
+ 1 -> allTests.filter { it.difficulty == TestDifficulty.MEDIUM }
+ 2 -> allTests.filter { it.difficulty == TestDifficulty.HARD }
+ else -> allTests
+ }
+ 
+ Column(
+ modifier = Modifier.fillMaxSize()
+ ) {
+ MockMateTopBar(
+ title = "Mock Tests",
+ showBackButton = true,
+ onBackClick = onNavigateBack,
+ showSettings = true,
+ onSettingsClick = onSettingsClick
+ )
+ 
+ Column(
+ modifier = Modifier
+ .fillMaxSize()
+ .padding(bottom = 16.dp),
+ horizontalAlignment = Alignment.CenterHorizontally
+ ) {
+ Text(
+ text = "Select Test Difficulty",
+ style = MaterialTheme.typography.headlineSmall,
+ fontWeight = FontWeight.Bold,
+ modifier = Modifier.padding(horizontal = 16.dp, vertical = 16.dp)
+ )
+ 
+ TabRow(selectedTabIndex = selectedTabIndex) {
+ Tab(
+ selected = selectedTabIndex == 0,
+ onClick = { selectedTabIndex = 0 },
+ text = { Text("Easy (30 min)") }
+ )
+ Tab(
+ selected = selectedTabIndex == 1,
+ onClick = { selectedTabIndex = 1 },
+ text = { Text("Medium (60 min)") }
+ )
+ Tab(
+ selected = selectedTabIndex == 2,
+ onClick = { selectedTabIndex = 2 },
+ text = { Text("Hard (90 min)") }
+ )
+ }
+ 
+ Spacer(modifier = Modifier.height(16.dp))
+ 
+ LazyColumn(
+ modifier = Modifier
+ .fillMaxSize()
+ .padding(horizontal = 16.dp)
+ ) {
+ items(filteredTests) { test ->
+ TestCard(
+ test = test,
+ onClick = { onTestSelected(test.id) }
+ )
+ }
+ }
+ }
+ }
