+@Composable
+fun TestNavigationFooter(
+ currentQuestionIndex: Int,
+ totalQuestions: Int,
+ onPreviousClick: () -> Unit,
+ onNextClick: () -> Unit,
+ onFinishClick: () -> Unit,
+ onBookmarkClick: () -> Unit,
+ onMarkForReviewClick: () -> Unit,
+ isBookmarked: Boolean,
